contentCss:
body {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
  line-height: 1.4;
  margin: 1rem;
}

table {
  border-collapse: collapse;
}

table th,
table td {
  border: 1px solid #ccc;
  padding: .4rem;
}

figure {
  display: table;
  margin: 1rem auto;
}

figure figcaption {
  color: #999;
  display: block;
  margin-top: .25rem;
  text-align: center;
}

hr {
  border-color: #ccc;
  border-style: solid;
  border-width: 1px 0 0 0;
}

code {
  background-color: #e8e8e8;
  border-radius: 3px;
  padding: .1rem .2rem;
}

.mce-content-body:not([dir=rtl]) {
  blockquote {
    border-left: 2px solid #ccc;
    margin-left: 1.5rem;
    padding-left: 1rem;
  }
}

// RTL
.mce-content-body[dir=rtl] {
  blockquote {
    border-right: 2px solid #ccc;
    margin-right: 1.5rem;
    padding-right: 1rem;
  }
}

lessVariables:
@background-color: #fff;
@toolbar-background-color: @background-color;
@menubar-background-color: @background-color;
@border-color: #ccc;
@color-black: #222f3e;
@color-white: #fff;
@color-tint: rgba(121, 174, 200, 1);
@color-error: #c00;
@color-success: #78AB46;
@base-value: 13px;
@control-border-radius: 4px;
@panel-border-radius: 4px;
@tinymce-border-radius: 4px;
@font-stack: Roboto,"Lucida Grande","DejaVu Sans","Bitstream Vera Sans",Verdana,Arial,sans-serif;
@font-weight-normal: 400;
@font-weight-bold: normal;
@toolbar-button-icon-color: rgba(0, 0, 0, 1);
@toolbar-button-text-color: rgba(0, 0, 0, 1);
@toolbar-button-hover-background-color: multiply(white, contrast(@background-color, fade(@color-black, 15), fade(@color-black, 85)));
@toolbar-button-hover-icon-color: rgba(0, 0, 0, 1);
@toolbar-button-hover-text-color: rgba(0, 0, 0, 1);
@toolbar-button-focus-background-color: @toolbar-button-hover-background-color;
@toolbar-button-focus-icon-color: rgba(0, 0, 0, 1);
@toolbar-button-focus-text-color: rgba(0, 0, 0, 1);
@toolbar-button-active-background-color: @toolbar-button-hover-background-color;
@toolbar-button-enabled-background-color: @toolbar-button-hover-background-color;
@toolbar-button-active-icon-color: rgba(0, 0, 0, 1);
@toolbar-button-active-text-color: rgba(0, 0, 0, 1);
@toolbar-button-enabled-icon-color: rgba(0, 0, 0, 1);
@toolbar-button-enabled-text-color: rgba(0, 0, 0, 1);
